hexo.extend.tag.register('black', function(args) {
  var splited = args.join(' ').split('|');
  var text = splited[0].trim();
  var title = "你知道的太多了";
  
  if (splited.length > 1) {
  	title = splited[1].trim();
  }

  var result = "<span title=\"" + title + "\" style=\"background-color: #252525; color: #252525;\"" + " onclick=\"if (/(iPhone|iPad|iPod|iOS|Android)/i.test(navigator.userAgent)) {this.style.background=\'none\';}\">" + text + "</span>";
  
  return result;
});

hexo.extend.tag.register('rubyb', function(args) {
  var splited = args.join(' ').split('|');
  var origin = splited[0].trim();
  var title = "你知道的太多了";
  var ruby = origin;
  
  if (splited.length > 1) {
  	ruby = splited[1].trim();
  }

  var result = "<ruby>" + origin + "<rp> (</rp><rt><span title=\"" + title + "\" style=\"background-color: #252525; color: #252525;\"" + " onclick=\"if (/(iPhone|iPad|iPod|iOS|Android)/i.test(navigator.userAgent)) {this.style.background=\'none\';}\">" + ruby + "</span></rt><rp>) </rp></ruby>";
  
  return result;
});

hexo.extend.tag.register('done', function(args) {
  return "<input type='checkbox' onclick='return false;' checked>";
});

hexo.extend.tag.register('todo', function(args) {
  return "<input type='checkbox' onclick='return false;'>";
});
